  #star {
    padding: 5px 0;
    display: flex;
    align-items: center;
    color: #b1b1b1;
    .class-hover {
      &:hover {
        color: #fc0;
      }
    }
    .satisfy {
      color: #fc0;
    }
    @font-face {
      font-family: 'iconfont';
      src: url(data:font/truetype;charset=utf-8;base64,) format('truetype');
