<template>
  <div class="backToTop" @click="backToTop()">
    <i class="icon iconfont icon-scrollTop"></i>
  </div>
</template>

<script type="text/ecmascript-6">

  export default {
    methods: {
      backToTop () {
        let timer = setInterval(() => {
          var top = document.getElementsByClassName('content')[0].scrollTop
          let speed = Math.ceil(top / 5)
          document.getElementsByClassName('content')[0].scrollTop = top - speed
          if (top === 0) {
            this.$emit('hide', false)
            clearInterval(timer)
          }
        }, 20)
      }
    }
  }
</script>

<style lang="less" scoped>
  /* 加载字体样式 */
  .backToTop {
    cursor: pointer;
    position: fixed;
    right: 5%;
    bottom: 15vh;
    width: 10vw;
    height: 10vw;
    border-radius: 50%;
    background-color: rgba(78, 78, 78, 0.45);
    display: flex;
    .iconfont {
      margin: auto;
      font-size: 2.5rem;
      color: #4c4949;
    }
    @font-face {
      font-family: 'iconfont';
      src: url(data:font/truetype;charset=utf-8;base64,) format('truetype');
      font-weight: normal;
      font-style: normal;
    }
    .iconfont {
      font-family:"iconfont";
      font-style:normal;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }

    .icon-scrollTop:before { content: "\e600"; }
  }

  @media screen and (min-width: 640px) {
    .backToTop {
      right: 50%;
      transform: translate(300px, 0);
      bottom: 120px;
      width: 60px;
      height: 60px;
      .iconfont {
        font-size: 3.5rem;
      }
    }
  }
</style>
